export default class Animal {
    constructor(name, species, gender) {
        this.name = name
        this.species = species
        this.gender = gender
    }

    speak() {
        console.log('Speaking...')
    }

    walk() {
        console.log('Walking...')
    }
}

export class Human extends Animal {
    constructor(name, age, gender) {
        super(name, 'human', gender)
        this.age = age
    }

    speak() {
        console.log('Human speaking...')
    }

    walk() {
        console.log('Human walking...')
    }
}

export class Lion extends Animal {
    constructor(name, age, gender) {
        super(name, 'lion', gender)
        this.age = age
    }

    speak() {
        console.log('Lion roaring...')
    }

    walk() {
        console.log('Lion walking...')
    }
}