import express from "express";
import {
  userLogin,
  userSignUp,
  nameExtension,
  searchExtension,
  homeHandler,
  usersHandler,
  changeHandler,
  deleteHandler,
  staffHandler,
  bookHandler
} from "./extensions.js";

const router = express.Router();

router.get("/login", userLogin);
router.get("/signup", userSignUp);
router.get("/user/:name", nameExtension);
router.get("/staff/:name/:id", staffHandler);
router.get("/search", searchExtension);
router.get("/", homeHandler);
router.get("/book/:book", bookHandler);
router.post("/users", usersHandler);
router.put("/users/:id", changeHandler);
router.delete("/users/:id", deleteHandler);

export default router;
